INSERT INTO SPEAKER(ID, FIRST_NAME, LAST_NAME, TWITTER, BIO) values (0, 'Jürgen', 'Höller', 'springjuergen', 'Polishing code since 2002. Spring Framework gatekeeper. Taught Chuck Norris how to code.');
INSERT INTO SPEAKER(ID, FIRST_NAME, LAST_NAME, TWITTER, BIO) values (1, 'Dave', 'Syer', 'david_syer', 'Founder and contributor to Spring Batch, lead of Spring Security OAuth, and an active contributor to Spring Integration, Spring Framework, Spring AMQP, Spring Security. Experienced, delivery-focused architect and development manager. Has designed and built successful enterprise software solutions using Spring, and implemented them in major institutions worldwide.');
INSERT INTO SPEAKER(ID, FIRST_NAME, LAST_NAME, TWITTER, BIO) values (2, 'Rob', 'Winch', 'rob_winch', 'Open source enthusiast, @SpringSecurity Project Lead, @SpringLDAP Project Lead, @SpringFramework commiter, Employed by @pivotal, Author');
INSERT INTO SPEAKER(ID, FIRST_NAME, LAST_NAME, TWITTER, BIO) values (3, 'Brian', 'Clozel', 'bclozel', 'Spring Framework committer, Pivotal inc.');
INSERT INTO SPEAKER(ID, FIRST_NAME, LAST_NAME, TWITTER, BIO) values (4, 'Sébastien', 'Deleuze', 'sdeleuze', 'Spring framework commiter @ Pivotal and Dart Google Developer Expert');
INSERT INTO SPEAKER(ID, FIRST_NAME, LAST_NAME, TWITTER, BIO) values (5, 'Andy', 'Wilkinson', 'ankinson', 'Spring Boot committer and Spring IO Platform lead at @Pivotal. iOS developer at @DeftMethods. Views, models, and controllers are all my own.');
INSERT INTO SPEAKER(ID, FIRST_NAME, LAST_NAME, TWITTER, BIO) values (6, 'Sam', 'Brannen', 'sam_brannen', 'Sam Brannen is a Senior Software Consultant with over 15 years'' experience, a graduate of the Georgia Tech College of Computing, and co-founder of Swiftmind, a software consulting agency in Zurich, Switzerland. At Swiftmind Sam helps international clients achieve best practices in agile software development, architecture, design, implementation, and testing of enterprise Java applications using the Spring Framework and a plethora of open source technologies. In his consulting role, Sam most enjoys leading work shops, code reviews, coaching, and training clients. Sam is a popular speaker at conferences on Java and Spring. He is also an active core committer for the Spring Framework, author of the Spring TestContext Framework, and was previously a core developer of SpringSource dm Server (a.k.a., Eclipse Virgo). Over the years, Sam has helped clients build applications in various business sectors ranging from e-commerce to banking, retail, automotive, and social communities. When not in front of his computer, Sam enjoys traveling and spending time with family and friends.');
INSERT INTO SPEAKER(ID, FIRST_NAME, LAST_NAME, TWITTER, BIO) values (7, 'Stephane', 'Maldini', 'smaldini', 'A Software Architect with extensive experience aligning various OSS technologies. Multi-tasker eating tech 24/7, Stephane is interested in cloud computing, data science and messaging. He is on a mission to help organisations transform their applications into consumer-grade software. He co-founded the Reactor Project to help developers create reactive, low-latency fast data architectures on the JVM and beyond.');
INSERT INTO SPEAKER(ID, FIRST_NAME, LAST_NAME, TWITTER, BIO) values (8, 'Michael', 'Minella', 'michaelminella', 'Michael Minella is a software engineer, teacher and author with over a decade of enterprise development experience. Michael was a member of the expert group for JSR-352 (java batch processing). He currently works for Pivotal as the project lead for the Spring Batch project as well as an instructor at DePaul University. Michael is the author of Pro Spring Batch from Apress and the popular Refcard JUnit and EasyMock. Outside of the daily grind, Michael enjoys spending time with his family and enjoys woodworking, photography and InfoSec hobbies.');
INSERT INTO SPEAKER(ID, FIRST_NAME, LAST_NAME, TWITTER, BIO) values (9, 'Mark', 'Fisher', 'm_f_', 'Mark has been a member of the Spring team for over 8 years, contributing to the Spring Framework and several other projects. He founded Spring Integration in 2007 and is one of the authors of Spring Integration in Action, published by Manning in 2012. Currently he co-leads Spring XD where he focuses on the distributed runtime and everything related to the flow of data.');
INSERT INTO SPEAKER(ID, FIRST_NAME, LAST_NAME, TWITTER, BIO) values (10, 'Gary', 'Russel', 'gprussel', 'Gary has been in software engineering, concentrating on Enterprise Integration, for over 30 years on various platforms, and in the Java space since the late ''90s. He has been developing with the Spring Framework since 2004. He has been a committer on the Spring Integration and Spring AMQP projects for over 4 years, and now leads both projects.');
